// 물론 둘다 else if 말고 그냥 if 문들로 해도 됨

// 근데 else if가 나은듯



document

.getElementsByTagName('form')[0]

.addEventListener('submit', function (e) {

if (document.getElementById('email').value == '') {

e.preventDefault();

