for (let i = 0; i < 탭버튼.length; i++) {
	탭버튼.eq(i).on("click", function () {
		탭열기(i);
	});
}

function 탭열기(구멍) {
	탭버튼.removeClass("orange");
	탭버튼.eq(구멍).addClass("orange");
