        } catch (Exception e) { // Exceptionì´ ë°œìƒí•œê±´ ChatThreadê°€ ì—°ê²°ì´ ëŠì–´ì§„ ê²°  

        } finally {  
            broadcast(name + "ë‹˜ì´ ì—°ê²°ì´ ëŠì–´ì¡ŒìŠµë‹ˆë‹¤.", false);  
            this.list.remove(this);  
            try {  
